import { View, Text, Button, TextInput } from 'react-native';
import auth from '@react-native-firebase/auth';
import { GoogleSignin } from '@react-native-community/google-signin';



    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('');

    const handleEmailSignUp = async () => {
  try {
    await auth().createUserWithEmailAndPassword(email, password);
    // You can navigate the user to your app's main screen after signing up here
  } catch (error) {
    console.error("Error signing up with email and password:", error);
  }
};


const handleGoogleSignUp = async () => {
  try {
    // Get the users ID token
    const { idToken } = await GoogleSignin.signIn();

    // Create a Google credential with the token
    const googleCredential = auth.GoogleAuthProvider.credential(idToken);

    // Sign-in the user with the credential
    await auth().signInWithCredential(googleCredential);
    // You can navigate the user to your app's main screen after signing up here
  } catch (error) {
    console.error("Error signing up with Google:", error);
  }
};


export function SignUp() {
  return (
    <View style={styles.container}>
      <Text style={styles.heading}>Sign Up for TSM2.0</Text>
      <TextInput
        placeholder="Email"
        value={email}
        onChangeText={setEmail}
        style={{ borderWidth: 1, width: 200, marginBottom: 10 }}
      />
      <TextInput
        placeholder="Password"
        value={password}
        onChangeText={setPassword}
        secureTextEntry
        style={{ borderWidth: 1, width: 200, marginBottom: 10 }}
      />
      <Button title="Sign Up with Email" onPress={handleEmailSignUp} />
      <Button title="Sign Up with Google" onPress={handleGoogleSignUp} />
    </View>
  );
};
