import React, { useState, useEffect, useRef } from 'react';
import { View, Image, StyleSheet, FlatList, Modal, TouchableOpacity } from 'react-native';
import { FontAwesome } from '@expo/vector-icons';
import { Video } from 'expo-av';

export function Gallery() {
    const [images, setImages] = useState([]); // Für Bilder
    const [videos, setVideos] = useState([]); // Für Videos
    const [selectedMedia, setSelectedMedia] = useState(null); 
    const [showModal, setShowModal] = useState(false);
    const [isPlaying, setIsPlaying] = useState(false);
    const slideShowInterval = useRef(null);

    useEffect(() => {
        fetchMedia();
    }, []);

    const fetchMedia = async () => {
        // Bilder abrufen
        try {
            const imageResponse = await fetch('https://www.sbdci.de/kpw/get_images.php'); 
            const imageData = await imageResponse.json();
            setImages(imageData);
        } catch (error) {
            console.error("Error fetching images:", error);
        }

        // Videos abrufen
        try {
            const videoResponse = await fetch('https://www.sbdci.de/kpw/get_vid.php');
            const videoData = await videoResponse.json();
            setVideos(videoData);
        } catch (error) {
            console.error("Error fetching videos:", error);
        }
    };

    const combinedData = [...images, ...videos];

    const renderMediaItem = ({ item }) => {
        const isVideo = item.endsWith('.mp4'); 

        return (
            <TouchableOpacity onPress={() => handleMediaPress(item)}>
                {isVideo ? (
                    <Video
                        source={{ uri: `https://www.sbdci.de/kpw/upload/${item}` }}
                        style={styles.image}
                        shouldPlay={false}
                        resizeMode="cover"
                        useNativeControls={false}
                    />
                ) : (
                    <Image
                        source={{ uri: `https://www.sbdci.de/kpw/upload/${item}` }}
                        style={styles.image}
                    />
                )}
            </TouchableOpacity>
        );
    };

    const handleMediaPress = (item) => {
        setSelectedMedia(item);
        setShowModal(true);
    }

    const handleNext = () => {
        const currentIndex = images.indexOf(selectedImage);
        const nextIndex = (currentIndex + 1) % images.length;
        setSelectedImage(images[nextIndex]);
    }

    const handlePrev = () => {
        const currentIndex = images.indexOf(selectedImage);
        const prevIndex = (currentIndex - 1 + images.length) % images.length;
        setSelectedImage(images[prevIndex]);
    }

    const togglePlay = () => {
        setIsPlaying(!isPlaying);
    }

    const closeModal = () => {
        setShowModal(false);
        setIsPlaying(false);
        clearInterval(slideShowInterval.current);
    }

    return (
        <View style={styles.container}>
            <FlatList
                data={combinedData}
                keyExtractor={(item) => item}
                renderItem={renderMediaItem}
                numColumns={3} 
            />

            {showModal && (
                <Modal
                    animationType="slide"
                    transparent={false}
                    visible={showModal}
                >
                    <View style={styles.modalContainer}>
                        <FontAwesome name="times" size={24} color="black" style={styles.closeIcon} onPress={() => setShowModal(false)} />

                        {/* Hier können Sie die Modalansicht basierend auf dem Medientyp (Bild oder Video) anpassen */}
                        <Image 
                            source={{ uri: `https://www.sbdci.de/kpw/upload/${selectedMedia}` }} 
                            style={styles.modalImage} 
                        />

                        <View style={styles.controls}>
                        <FontAwesome name="arrow-left" size={24} color="black" onPress={handlePrev} />
                        <FontAwesome name={isPlaying ? "pause" : "play"} size={24} color="black" onPress={togglePlay} />
                        <FontAwesome name="arrow-right" size={24} color="black" onPress={handleNext} />
                    </View>
                    </View>
                </Modal>
            )}
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        overflow: "hidden",
        marginBottom: 65,
    },
    image: {
        width: 100,
        height: 150,
        margin: 5,
    },
    modalContainer: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
    modalImage: {
        width: '90%',
        height: '70%',
        resizeMode: 'contain',
    },
    controls: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        width: '60%',
        marginTop: 20,
    },
    closeIcon: {
        position: 'absolute',
        top: 20,
        right: 20,
    },
});
