import React, { useState } from 'react';
import { View, Image, TextInput, StyleSheet, TouchableOpacity, Text, Dimensions } from 'react-native';
import { FontAwesome5 } from '@expo/vector-icons'; 

export function ImageEditor({ route, navigation }) {
    const { imageUri } = route.params;
    const [isEditing, setIsEditing] = useState(false);
    const [text, setText] = useState('');
    const [fontSize, setFontSize] = useState(20);

    const onClose = () => {
        navigation.goBack();
    }

    const onSave = () => {
        setIsEditing(false);
    }

    return (
        <View style={styles.editorContainer}>
            <Image source={{ uri: imageUri }} style={{ width: Dimensions.get('window').width, height: Dimensions.get('window').height - 100 }} />

            {isEditing && (
                <TextInput
                    style={{...styles.textInput, fontSize: fontSize}}
                    value={text}
                    onChangeText={setText}
                    placeholder="F端ge Text hinzu"
                />
            )}

            {!isEditing && (
                <Text style={{...styles.overlayText, fontSize: fontSize}}>{text}</Text>
            )}

            <View style={styles.buttonContainer}>
                {isEditing ? (
                    <TouchableOpacity onPress={onSave}>
                        <FontAwesome5 name="check" size={24} color="green" />
                    </TouchableOpacity>
                ) : (
                    <TouchableOpacity onPress={() => setIsEditing(true)}>
                        <FontAwesome5 name="pen" size={24} color="blue" />
                    </TouchableOpacity>
                )}
            </View>

            <TouchableOpacity style={styles.closeButton} onPress={onClose}>
                <FontAwesome5 name="times" size={24} color="black" />
            </TouchableOpacity>
        </View>
    );
}

const styles = StyleSheet.create({
    editorContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    textInput: {
    position: 'absolute',
    color: 'white',
    borderColor: 'white',
    borderWidth: 1,
    borderRadius: 4,
    zIndex: 5, // Diese Zeile hinzuf端gen
},
overlayText: {
    position: 'absolute',
    color: 'white',
    zIndex: 5, // Diese Zeile hinzuf端gen
},
    buttonContainer: {
    position: 'absolute',
    right: 10,
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 10, // Diese Zeile hinzuf端gen oder aktualisieren
},
    closeButton: {
        position: 'absolute',
        top: 20, // Oben positioniert
        right: 20, // Rechts positioniert
    },
});
