import React, { useState } from 'react';
import { TouchableOpacity, Text, View, TextInput } from 'react-native';
import { useNavigation } from '@react-navigation/native';

export function TextEditor({ 
    text, 
    setText, 
    textColor, 
    setTextColor, 
    fontFamily, 
    setFontFamily, 
    fontSize, 
    setFontSize,
    route
}) {
    const navigation = useNavigation();
    const { imageUri } = route.params;

    const [localtextColor, setlocalTextColor] = useState(textColor); 
    const [localfontFamily, setlocalFontFamily] = useState(fontFamily);
    const [localfontSize, setlocalFontSize] = useState(fontSize);

    console.log("Received imageUri in TextEditor: ", imageUri);

    const handleNavigation = () => {
        setTextColor(localtextColor);
        setFontFamily(localfontFamily);
        setFontSize(localfontSize);
        navigation.navigate('ImageEditor', { imageUri: imageUri });
    }

    return (
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
            <TextInput
                style={{
                    color: localtextColor,
                    fontFamily: localfontFamily,
                    fontSize: localfontSize,
                }}
                value={text}
                onChangeText={setText}
                multiline
                placeholder="Geben Sie Ihren Text hier ein..."
            />

            {/* Hier können Sie weitere Optionen hinzufügen, um Schriftfarbe, Schriftart usw. zu ändern. */}

            <TouchableOpacity
                style={{ marginTop: 10, padding: 10, backgroundColor: 'blue', borderRadius: 5 }}
                onPress={handleNavigation}
            >
                <FontAwesome5 name="check" size={24} color="black" />
            </TouchableOpacity>
        </View>
    );
}


const styles = StyleSheet.create({
    inputText: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
        backgroundColor: 'transparent', // Hintergrundfarbe, falls gewünscht
        position: 'absolute', // Absolute Positionierung hinzufügen
        top: 0, 
        bottom: 0, 
        left: 0, 
        right: 0,
    },
    modalInputText: {
        flex: 1, 
        backgroundColor: "white",
        padding: 35,
        justifyContent: "center",
        alignItems: "center",
        margin: 0, // Entfernt jeglichen Abstand
        width: '100%' // Stellt sicher, dass es die volle Breite einnimmt
    },
    dropdownContainer: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        width: '100%',
        marginBottom: 20,
    },
    dropdownItem: {  
        width: '30%', 
        margin: 5,
    },
});
