import React, { useState } from 'react';
import { View, TouchableOpacity, StyleSheet, Alert, Image, Button } from 'react-native';
import { FontAwesome } from '@expo/vector-icons'; 
import * as ImagePicker from 'expo-image-picker';

export function Camera({ onCapture = () => {}, onClose }) {
    const [selectedImageUri, setSelectedImageUri] = useState(null);

    const openCamera = async () => {
        const permissionResult = await ImagePicker.requestCameraPermissionsAsync();
        if (!permissionResult.granted) {
            Alert.alert('Permission to access camera is required!');
            return;
        }

        const pickerResult = await ImagePicker.launchCameraAsync();
        if (pickerResult.canceled) {
            return;
        }

        const imageUri = pickerResult.assets[0].uri;
        console.log("Image loaded!");
        console.log(imageUri)
        onCapture(imageUri);
        setSelectedImageUri(imageUri);
    };

    const openPhotoLibrary = async () => {
        const permissionResult = await ImagePicker.requestMediaLibraryPermissionsAsync();
        if (!permissionResult.granted) {
            Alert.alert('Permission to access media library is required!');
            return;
        }

        const pickerResult = await ImagePicker.launchImageLibraryAsync();
        if (pickerResult.canceled) {
            return;
        }

        const imageUri = pickerResult.assets[0].uri;
        console.log("Image loaded!");
        console.log(imageUri)
        onCapture(imageUri);
        setSelectedImageUri(imageUri);
    };

    return (
        <View style={styles.cameraContainer}>
            <View style={styles.buttonsContainer}>
                <TouchableOpacity style={styles.iconButton} onPress={openPhotoLibrary}>
                    <FontAwesome name="upload" size={32} />
                </TouchableOpacity>
                <TouchableOpacity style={styles.iconButton} onPress={openCamera}>
                    <FontAwesome name="camera" size={32} />
                </TouchableOpacity>
            </View>

            {/* Display selected image directly */}
            {selectedImageUri && (
                <View style={{flex: 1, justifyContent: 'center', alignItems: 'center'}}>
                    <Image 
                        source={{ uri: selectedImageUri }}
                        style={{ width: '100%', height: '100%', resizeMode: 'contain' }}
                    />
                    <Button title="SchlieÃŸen" onPress={() => setSelectedImageUri(null)} />
                </View>
            )}
        </View>
    );
}

const styles = StyleSheet.create({
    cameraContainer: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center'
    },
    buttonsContainer: {
        flexDirection: 'row',
        justifyContent: 'space-around',
        width: '100%',
        padding: 20
    },
    iconButton: {
        alignItems: 'center',
        justifyContent: 'center',
        padding: 10
    }
});
