import React, { useState } from 'react';
import { View, TextInput, TouchableOpacity, StyleSheet } from 'react-native';
import { FontAwesome5 } from '@expo/vector-icons';
import { FontColorDropdown } from './fontHandler/FontColorDropdown.js';
import { FontFamilyDropdown } from './fontHandler/FontFamilyDropdown.js';
import { FontSizeDropdown } from './fontHandler/FontSizeDropdown.js';
import { useNavigation } from '@react-navigation/native';

export function TextEditor({ 
    text, 
    setText, 
    textColor, 
    setTextColor, 
    fontFamily, 
    setFontFamily, 
    fontSize, 
    setFontSize,
    imageUri
}) {
    const navigation = useNavigation();
    const [localtextColor, setlocalTextColor] = useState('black'); 
    const [localfontFamily, setlocalFontFamily] = useState('Arial');
    const [localfontSize, setlocalFontSize] = useState(20);
    return (
        <View style={styles.inputText}>
            <View style={styles.modalInputText}>
                <View style={styles.dropdownContainer}>
                    <FontSizeDropdown 
                        style={styles.dropdownItem}
                        value={fontSize}
                        onFontSizeChange={value => setlocalFontSize(value)}
                    />
                    <FontColorDropdown 
                        style={styles.dropdownItem}
                        color={textColor}
                        onFontColorChange={color => setlocalTextColor(color)}
                    />
                    <FontFamilyDropdown 
                        style={styles.dropdownItem}
                        fontFamily={fontFamily}
                        onFontFamilyChange={font => setlocalFontFamily(font)}
                    />
                </View>
                <TextInput
                    value={text}
                    onChangeText={setText}
                    placeholder="Füge Text hinzu"
                    multiline={true}
                    style={{ 
                        color: localtextColor, 
                        fontSize: localfontSize, 
                        fontFamily: localfontFamily, 
                        width: '100%',
                        borderColor: '#ccc',
                        borderWidth: 1,
                        marginBottom: 20,
                    }}
                />
                <TouchableOpacity
                    style={{ marginTop: 10 }}
                    onPress={() => navigation.navigate('ImageEditor', { imageUri: imageUri })}
                >
                    <FontAwesome5 name="check" size={24} color="black" />
                </TouchableOpacity>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    inputText: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
        backgroundColor: 'transparent', // Hintergrundfarbe, falls gewünscht
        position: 'absolute', // Absolute Positionierung hinzufügen
        top: 0, 
        bottom: 0, 
        left: 0, 
        right: 0,
        border: "solid red"
    },
    modalInputText: {
        flex: 1, 
        backgroundColor: "white",
        padding: 35,
        justifyContent: "center",
        alignItems: "center",
        margin: 0, // Entfernt jeglichen Abstand
        width: '100%' // Stellt sicher, dass es die volle Breite einnimmt
    },
    dropdownContainer: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        width: '100%',
        marginBottom: 20,
    },
    dropdownItem: {  
        width: '30%', 
        margin: 5,
    },
});
