import React from 'react';
import { View, Image, StyleSheet, TouchableOpacity, Text } from 'react-native';

export function ImageEditor({ route, navigation }) {
    const { imageUri } = route.params;  // Hier holen Sie die Image-URI aus den Route-Parametern
    console.log("Image URI:", imageUri);

    const onClose = () => {
        navigation.goBack();  // Navigation zurück zur vorherigen Ansicht
    }

    return (
        <View style={styles.editorContainer}>
            {imageUri && (
                <View >
                    <Image 
                        source={{ uri: imageUri }}
                        style={{ width: 500, height: 500, resizeMode: 'contain' }}
                    />
                    <TouchableOpacity style={styles.closeButton} onPress={onClose}>
                    <Text style={styles.closeButtonText}>Schließen</Text>
                    </TouchableOpacity>
                </View>
            )}
        </View>
    );
}

const styles = StyleSheet.create({
    editorContainer: {
        flex: 1,
        alignItems: 'center',
    },
    closeButton: {
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#DDDDDD',
        padding: 10,
        marginTop: 10,  // Um etwas Abstand zwischen dem Bild und dem Button zu schaffen
        borderRadius: 4,
    },
    closeButtonText: {
        color: '#333',
        fontWeight: 'bold',
    }
});
