                    {usingWebcam && (
                        <View>
                            {/* Webcam component */}
                            <TouchableOpacity style={styles.iconButton} onPress={capture}>
                                <FontAwesome name="camera" size={32} />
                            </TouchableOpacity>
                        </View>
                    )}
                </View>
            )}
        </View>
    );
}

const styles = StyleSheet.create({
    cameraContainer: {
        flex: 1,
